// src/components/ForgotPassword.jsx
import React, { useState } from "react";

/**
 * Props:
 * - onBack(): function to call to go back to login screen
 */
export default function ForgotPassword({ onBack }) {
  const [step, setStep] = useState(0);
  const [email, setEmail] = useState("");
  const [code, setCode] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  const base = "http://localhost:4000";

  async function sendOtp(e) {
    e?.preventDefault();
    setMessage("");
    if (!email) return setMessage("Please enter your email");
    setLoading(true);
    try {
      const res = await fetch(`${base}/send-otp`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email }),
      });
      const data = await res.json();
      if (!res.ok) {
        setMessage(data.error || "Failed to send OTP");
      } else {
        setMessage("OTP sent ✅ Check your email");
        setStep(1);
      }
    } catch (err) {
      console.error(err);
      setMessage("Network error while sending OTP");
    } finally {
      setLoading(false);
    }
  }

  async function verifyOtp(e) {
    e?.preventDefault();
    setMessage("");
    if (!code) return setMessage("Enter the OTP code");
    setLoading(true);
    try {
      const res = await fetch(`${base}/verify-otp`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, code }),
      });
      const data = await res.json();
      if (!res.ok) {
        setMessage(data.error || "Invalid OTP");
      } else {
        setMessage("OTP verified ✅ Please set a new password");
        setStep(2);
      }
    } catch (err) {
      console.error(err);
      setMessage("Network error verifying OTP");
    } finally {
      setLoading(false);
    }
  }

  async function resetPassword(e) {
    e?.preventDefault();
    setMessage("");
    if (!newPassword || newPassword.length < 6) return setMessage("Password must be at least 6 characters");
    setLoading(true);
    try {
      const res = await fetch(`${base}/reset-password`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, code, newPassword }),
      });
      const data = await res.json();
      if (!res.ok) {
        setMessage(data.error || "Failed to reset password");
      } else {
        setMessage("Password updated ✅ You can now login with the new password");
        // go back to login after a short delay
        setTimeout(() => {
          setStep(0);
          setEmail("");
          setCode("");
          setNewPassword("");
          if (typeof onBack === "function") onBack();
        }, 900);
      }
    } catch (err) {
      console.error(err);
      setMessage("Network error resetting password");
    } finally {
      setLoading(false);
    }
  }

  return (
    <div style={{ padding: 20, color: "#fff", display: "flex", justifyContent: "center" }}>
      <div style={{ width: 360 }}>
        <h2 style={{ marginBottom: 12 }}>Forgot Password</h2>

        {step === 0 && (
          <form onSubmit={sendOtp}>
            <div className="input-box">
              <input
                type="email"
                required
                placeholder=" "
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <label>Email</label>
            </div>

            <div style={{ marginTop: 12 }}>
              <button className="btn" type="submit" disabled={loading}>
                {loading ? "Sending..." : "Send OTP"}
              </button>
            </div>
          </form>
        )}

        {step === 1 && (
          <form onSubmit={verifyOtp}>
            <p style={{ marginBottom: 8 }}>OTP sent to <strong>{email}</strong></p>
            <div className="input-box">
              <input
                type="text"
                required
                placeholder=" "
                value={code}
                onChange={(e) => setCode(e.target.value)}
              />
              <label>OTP Code</label>
            </div>

            <div style={{ marginTop: 12 }}>
              <button className="btn" type="submit" disabled={loading}>
                {loading ? "Verifying..." : "Verify OTP"}
              </button>
            </div>
          </form>
        )}

        {step === 2 && (
          <form onSubmit={resetPassword}>
            <p style={{ marginBottom: 8 }}>Reset password for <strong>{email}</strong></p>

            <div className="input-box">
              <input
                type="password"
                required
                placeholder=" "
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
              />
              <label>New Password</label>
            </div>

            <div style={{ marginTop: 12 }}>
              <button className="btn" type="submit" disabled={loading}>
                {loading ? "Updating..." : "Reset Password"}
              </button>
            </div>
          </form>
        )}

        {message && <p style={{ marginTop: 12, color: "#fff" }}>{message}</p>}

        <p style={{ marginTop: 12 }}>
          <a href="#" onClick={(e) => { e.preventDefault(); if (typeof onBack === 'function') onBack(); }}>
            ← Back to Login
          </a>
        </p>
      </div>
    </div>
  );
}
