// src/App.jsx
import React, { useState } from "react";
import "./styles/style.css";

import Login from ".components/Login";
import Signup from ".components/Signup";

export default function App() {
  const [active, setActive] = useState(false); 
  // active = false → Login shown
  // active = true  → Signup shown

  return (
    <div className={`container ${active ? "active" : ""}`}>
      {/* Background shapes */}
      <div className="curved-shape"></div>
      <div className="curved-shape2"></div>

      {/* LOGIN FORM */}
      <Login onSwitchToSignup={() => setActive(true)} />

      {/* LOGIN INFO PANEL */}
      <div className="info-content Login">
        <h2 className="animation" style={{ "--S": 1 }}>Welcome Back!</h2>
        <p className="animation" style={{ "--S": 2 }}>
          To stay connected with us, please login with your personal info.
        </p>
      </div>

      {/* SIGNUP FORM */}
      <Signup onSwitchToLogin={() => setActive(false)} />

      {/* SIGNUP INFO PANEL */}
      <div className="info-content Register">
        <h2 className="animation" style={{ "--li": 1 }}>WELCOME!</h2>
        <p className="animation" style={{ "--li": 2 }}>
          Join us by creating your account and start your journey today.
        </p>
      </div>
    </div>
  );
}
